"""
CONTACT MANAGER SYSTEM
A comprehensive contact management application using dictionaries and sets.
"""

# Contact database - dictionary of dictionaries
contacts = {
    "alice_j": {
        "name": "Alice Johnson",
        "phone": "555-0101",
        "email": "alice@techcorp.com",
        "tags": {"work", "developer", "friend"}
    },
    "bob_s": {
        "name": "Bob Smith",
        "phone": "555-0102",
        "email": "bob@email.com",
        "tags": {"family", "brother"}
    },
    "carol_d": {
        "name": "Carol Davis",
        "phone": "555-0103",
        "email": "carol@techcorp.com",
        "tags": {"work", "manager"}
    }
}

def add_contact(contact_id, name, phone, email, tags=None):
    """Add a new contact to the database."""
    if contact_id in contacts:
        return False, "Contact ID already exists"

    contacts[contact_id] = {
        "name": name,
        "phone": phone,
        "email": email,
        "tags": set(tags) if tags else set()
    }
    return True, "Contact added successfully"

def get_contact(contact_id):
    """Retrieve a contact by ID."""
    return contacts.get(contact_id)

def update_contact(contact_id, **updates):
    """Update contact information."""
    if contact_id not in contacts:
        return False, "Contact not found"

    contact = contacts[contact_id]

    # Update allowed fields
    for field in ['name', 'phone', 'email']:
        if field in updates:
            contact[field] = updates[field]

    # Handle tags specially (set operations)
    if 'add_tags' in updates:
        contact['tags'].update(updates['add_tags'])
    if 'remove_tags' in updates:
        contact['tags'].difference_update(updates['remove_tags'])

    return True, "Contact updated successfully"

def delete_contact(contact_id):
    """Remove a contact from the database."""
    if contact_id in contacts:
        contacts.pop(contact_id)
        return True, "Contact deleted successfully"
    return False, "Contact not found"

def search_by_tag(tag):
    """Find all contacts with a specific tag."""
    return {cid: contact for cid, contact in contacts.items() 
            if tag in contact['tags']}

def search_by_name(query):
    """Find contacts whose name contains the query string."""
    query_lower = query.lower()
    return {cid: contact for cid, contact in contacts.items() 
            if query_lower in contact['name'].lower()}

def get_all_tags():
    """Get set of all unique tags used."""
    all_tags = set()
    for contact in contacts.values():
        all_tags.update(contact['tags'])
    return all_tags

def get_email_domains():
    """Get set of all unique email domains."""
    return {contact['email'].split('@')[1] for contact in contacts.values()}

def count_by_tag():
    """Count how many contacts have each tag."""
    tag_counts = {}
    for contact in contacts.values():
        for tag in contact['tags']:
            tag_counts[tag] = tag_counts.get(tag, 0) + 1
    return tag_counts

def display_contact(contact_id, contact):
    """Pretty print a contact."""
    print(f"\nID: {contact_id}")
    print(f"Name: {contact['name']}")
    print(f"Phone: {contact['phone']}")
    print(f"Email: {contact['email']}")
    print(f"Tags: {', '.join(sorted(contact['tags'])) if contact['tags'] else 'None'}")

def display_statistics():
