    print(f"\nEmail domains: {', '.join(sorted(get_email_domains()))}")

    print("\nContacts per tag:")
    tag_counts = count_by_tag()
    for tag, count in sorted(tag_counts.items()):
        print(f"  {tag}: {count}")

