def export_by_tag(tag):
    """Return sorted list of names with the given tag."""
    names = [contact['name'] for contact in contacts.values() 
             if tag in contact.get('tags', set())]
    return sorted(names)

def find_duplicates():
    """Find contacts with duplicate phone or email."""
    duplicates = {'phone': {}, 'email': {}}

    for cid, contact in contacts.items():
        phone = contact['phone']
        email = contact['email']

        # Track phone duplicates
        if phone not in duplicates['phone']:
            duplicates['phone'][phone] = []
        duplicates['phone'][phone].append((cid, contact['name']))

        # Track email duplicates
        if email not in duplicates['email']:
            duplicates['email'][email] = []
        duplicates['email'][email].append((cid, contact['name']))

    # Filter to only actual duplicates (more than 1)
    phone_dupes = {k: v for k, v in duplicates['phone'].items() if len(v) > 1}
    email_dupes = {k: v for k, v in duplicates['email'].items() if len(v) > 1}

    return {'phone': phone_dupes, 'email': email_dupes}

def merge_tags(old_tag, new_tag):
    """Replace old_tag with new_tag in all contacts."""
    count = 0
    for contact in contacts.values():
        if old_tag in contact.get('tags', set()):
            contact['tags'].discard(old_tag)
            contact['tags'].add(new_tag)
            count += 1
    return count

def get_contacts_without_tags():
    """Return contacts that have no tags."""
    return {cid: contact for cid, contact in contacts.items() 
            if not contact.get('tags', set())}

