from flask import Flask, jsonify, request
from datetime import datetime

app = Flask(__name__)

# In-memory blog posts
posts = [
    {
        'id': 1,
        'title': 'First Post',
        'content': 'Hello World!',
        'author': 'Admin',
        'created_at': '2024-01-01T10:00:00'
    }
]

def validate_post(data):
    """Validate post data"""
    errors = []

    if not data:
        return ['No data provided']

    if 'title' not in data or not data['title'].strip():
        errors.append('Title is required')

    if 'content' not in data or not data['content'].strip():
        errors.append('Content is required')

    if 'title' in data and len(data['title']) > 200:
        errors.append('Title must be less than 200 characters')

    return errors

@app.route('/')
def home():
    """API documentation"""
    return jsonify({
        'name': 'Blog API',
        'version': '1.0',
        'endpoints': {
            'List posts': 'GET /api/posts',
            'Get post': 'GET /api/posts/<id>',
            'Create post': 'POST /api/posts',
            'Update post': 'PUT /api/posts/<id>',
            'Delete post': 'DELETE /api/posts/<id>'
        }
    })

@app.route('/api/posts', methods=['GET'])
def get_posts():
    """Get all posts with optional filtering"""
    # Query parameters
    author = request.args.get('author')
    limit = request.args.get('limit', type=int)

    filtered_posts = posts

    # Filter by author
    if author:
        filtered_posts = [p for p in filtered_posts if p.get('author', '').lower() == author.lower()]

    # Limit results
    if limit:
        filtered_posts = filtered_posts[:limit]

    return jsonify({
        'posts': filtered_posts,
        'count': len(filtered_posts),
        'total': len(posts)
    })

@app.route('/api/posts/<int:post_id>', methods=['GET'])
def get_post(post_id):
    """Get specific post by ID"""
    post = next((p for p in posts if p['id'] == post_id), None)

    if not post:
        return jsonify({'error': 'Post not found'}), 404

    return jsonify(post)

@app.route('/api/posts', methods=['POST'])
def create_post():
    """Create new blog post"""
    data = request.get_json()

    # Validate
    errors = validate_post(data)
    if errors:
        return jsonify({'errors': errors}), 400

    # Create new post
    new_post = {
        'id': max([p['id'] for p in posts]) + 1 if posts else 1,
        'title': data['title'].strip(),
        'content': data['content'].strip(),
        'author': data.get('author', 'Anonymous'),
        'created_at': datetime.now().isoformat()
    }

    posts.append(new_post)

    return jsonify(new_post), 201

@app.route('/api/posts/<int:post_id>', methods=['PUT'])
def update_post(post_id):
    """Update existing post"""
    post = next((p for p in posts if p['id'] == post_id), None)

    if not post:
        return jsonify({'error': 'Post not found'}), 404

    data = request.get_json()

    # Validate
    errors = validate_post(data)
    if errors:
        return jsonify({'errors': errors}), 400

    # Update post
    post['title'] = data['title'].strip()
    post['content'] = data['content'].strip()
    if 'author' in data:
        post['author'] = data['author']
    post['updated_at'] = datetime.now().isoformat()

    return jsonify(post)

@app.route('/api/posts/<int:post_id>', methods=['DELETE'])
def delete_post(post_id):
    """Delete post"""
    global posts
    post = next((p for p in posts if p['id'] == post_id), None)

    if not post:
        return jsonify({'error': 'Post not found'}), 404

    posts = [p for p in posts if p['id'] != post_id]

    return jsonify({
        'message': 'Post deleted successfully',
        'id': post_id
    })

@app.route('/api/stats', methods=['GET'])
def get_stats():
    """Get blog statistics"""
    authors = set(p.get('author', 'Anonymous') for p in posts)

    return jsonify({
        'total_posts': len(posts),
        'total_authors': len(authors),
        'authors': list(authors)
    })

# Error handlers
@app.errorhandler(404)
def not_found(error):
    return jsonify({'error': 'Endpoint not found'}), 404

@app.errorhandler(500)
def internal_error(error):
    return jsonify({'error': 'Internal server error'}), 500

if __name__ == '__main__':
    print("=== Blog API Server ===")
    print("Starting server on http://localhost:5000")
    print("\nEndpoints:")
    print("  GET    /api/posts       - List all posts")
    print("  GET    /api/posts/<id>  - Get specific post")
    print("  POST   /api/posts       - Create new post")
    print("  PUT    /api/posts/<id>  - Update post")
    print("  DELETE /api/posts/<id>  - Delete post")
    print("  GET    /api/stats       - Get statistics")
    print("\nExample POST request body:")
    print('  {"title": "My Post", "content": "Post content", "author": "John"}')
    print("\nRun with: python app.py")

    # Uncomment to run:
    # app.run(debug=True)
