from typing import List, Optional, Dict
from dataclasses import dataclass
from datetime import datetime

print("=== Task Management System ===")

# Data Model
@dataclass
class Task:
    """Task data model"""
    id: int
    title: str
    description: str
    completed: bool
    user_id: int
    created_at: datetime

print("âœ“ Data model defined")

# Repository Layer
class TaskRepository:
    """Handles data access for tasks"""

    def __init__(self):
        self._tasks: Dict[int, Task] = {}
        self._next_id = 1

    def create(self, title: str, description: str, user_id: int) -> Task:
        """Create new task"""
        task = Task(
            id=self._next_id,
            title=title,
            description=description,
            completed=False,
            user_id=user_id,
            created_at=datetime.now()
        )
        self._tasks[task.id] = task
        self._next_id += 1
        return task

    def find_by_id(self, task_id: int) -> Optional[Task]:
        """Find task by ID"""
        return self._tasks.get(task_id)

    def find_by_user(self, user_id: int, completed: Optional[bool] = None) -> List[Task]:
        """Find tasks by user, optionally filter by completed status"""
        tasks = [
            task for task in self._tasks.values()
            if task.user_id == user_id
        ]

        if completed is not None:
            tasks = [task for task in tasks if task.completed == completed]

        return tasks

    def list_all(self) -> List[Task]:
        """List all tasks"""
        return list(self._tasks.values())

    def update_status(self, task_id: int, completed: bool) -> bool:
        """Update task completion status"""
        task = self._tasks.get(task_id)
        if task:
            task.completed = completed
            return True
        return False

    def delete(self, task_id: int) -> bool:
        """Delete task"""
        if task_id in self._tasks:
            del self._tasks[task_id]
            return True
        return False

print("âœ“ Repository layer defined")

# Service Layer
class TaskService:
    """Business logic for task operations"""

    def __init__(self, task_repo: TaskRepository):
        self.task_repo = task_repo

    def create_task(self, title: str, description: str, user_id: int) -> Dict:
        """Create new task with validation"""
        # Validation
        if len(title.strip()) < 3:
            return {"error": "Title must be at least 3 characters"}

        if len(description.strip()) < 5:
            return {"error": "Description must be at least 5 characters"}

        # Create task
        task = self.task_repo.create(title.strip(), description.strip(), user_id)

        return {
            "success": True,
            "task": {
                "id": task.id,
                "title": task.title,
                "description": task.description,
                "completed": task.completed,
                "created_at": task.created_at.strftime("%Y-%m-%d %H:%M")
            }
        }

    def get_user_tasks(self, user_id: int, show_completed: bool = True) -> List[Dict]:
        """Get all tasks for a user"""
        completed_filter = None if show_completed else False
        tasks = self.task_repo.find_by_user(user_id, completed_filter)

        return [
            {
                "id": task.id,
                "title": task.title,
                "description": task.description,
                "completed": task.completed,
                "created_at": task.created_at.strftime("%Y-%m-%d")
            }
            for task in tasks
        ]

    def mark_completed(self, task_id: int, user_id: int) -> Dict:
        """Mark task as completed"""
        task = self.task_repo.find_by_id(task_id)

        if not task:
            return {"error": "Task not found"}

        # Check ownership
        if task.user_id != user_id:
            return {"error": "Not authorized to modify this task"}

        if task.completed:
            return {"error": "Task already completed"}

        self.task_repo.update_status(task_id, True)

        return {
            "success": True,
            "message": f"Task '{task.title}' marked as completed"
        }

    def mark_incomplete(self, task_id: int, user_id: int) -> Dict:
        """Mark task as incomplete"""
        task = self.task_repo.find_by_id(task_id)

        if not task:
            return {"error": "Task not found"}

        if task.user_id != user_id:
            return {"error": "Not authorized to modify this task"}

        self.task_repo.update_status(task_id, False)

        return {
            "success": True,
            "message": f"Task '{task.title}' marked as incomplete"
        }

    def delete_task(self, task_id: int, user_id: int) -> Dict:
        """Delete a task"""
        task = self.task_repo.find_by_id(task_id)

        if not task:
            return {"error": "Task not found"}

        if task.user_id != user_id:
            return {"error": "Not authorized to delete this task"}

        self.task_repo.delete(task_id)

        return {
            "success": True,
            "message": "Task deleted successfully"
        }

    def get_task_stats(self, user_id: int) -> Dict:
        """Get statistics about user's tasks"""
        all_tasks = self.task_repo.find_by_user(user_id)
        completed_tasks = self.task_repo.find_by_user(user_id, completed=True)
        pending_tasks = self.task_repo.find_by_user(user_id, completed=False)

        completion_rate = (
            (len(completed_tasks) / len(all_tasks) * 100)
            if all_tasks else 0
        )

        return {
            "total_tasks": len(all_tasks),
            "completed": len(completed_tasks),
            "pending": len(pending_tasks),
            "completion_rate": f"{completion_rate:.1f}%"
        }

print("âœ“ Service layer defined")

print("\n=== Testing Task Management System ===")

# Initialize
repo = TaskRepository()
service = TaskService(repo)

# Create tasks for user 1
print("\n1. Creating tasks...")

tasks_to_create = [
    ("Learn Python", "Complete Module 14 of the training course", 1),
    ("Build Portfolio", "Create a personal portfolio website", 1),
    ("Practice Algorithms", "Solve 10 coding challenges on LeetCode", 1),
    ("Read Documentation", "Read Flask and SQLAlchemy docs", 1),
]

for title, description, user_id in tasks_to_create:
    result = service.create_task(title, description, user_id)
    if result.get('success'):
        print(f"   âœ“ Created: {result['task']['title']}")
    else:
        print(f"   âœ— Error: {result['error']}")

# Test validation
print("\n2. Testing validation...")
invalid_result = service.create_task("AB", "Short", 1)
print(f"   {invalid_result}")

# List all tasks
print("\n3. Listing all tasks for user 1...")
tasks = service.get_user_tasks(1)
for i, task in enumerate(tasks, 1):
    status = "âœ“" if task['completed'] else "â—‹"
    print(f"   {status} [{task['id']}] {task['title']}")
    print(f"      {task['description']}")

# Mark some tasks as completed
print("\n4. Marking tasks as completed...")
result1 = service.mark_completed(1, 1)
print(f"   {result1['message'] if result1.get('success') else result1['error']}")

result2 = service.mark_completed(3, 1)
print(f"   {result2['message'] if result2.get('success') else result2['error']}")

# Show updated list
print("\n5. Updated task list...")
tasks = service.get_user_tasks(1)
for task in tasks:
    status = "âœ“" if task['completed'] else "â—‹"
    print(f"   {status} {task['title']}")

# Show only pending tasks
print("\n6. Pending tasks only...")
pending = service.get_user_tasks(1, show_completed=False)
for task in pending:
    print(f"   â—‹ {task['title']}")

# Get statistics
print("\n7. Task statistics...")
stats = service.get_task_stats(1)
for key, value in stats.items():
    print(f"   {key.replace('_', ' ').title()}: {value}")

# Test authorization
print("\n8. Testing authorization...")
result = service.mark_completed(1, 999)  # Wrong user
print(f"   {result.get('error', 'Should have failed!')}")

print("\nâœ“ Task Management System working perfectly!")
